export interface Props{
	title: string
	color?: string
}
const Header = (props: Props) => {
	return (
		<header>
		  {% raw %}
			<h1 style={{ color: prps.color ? props.color: "blue" }}>
				{props.title}
			</h1>
		  {% endraw %}
		</header>
	)
}
